{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "[ ! -d \"../../lambdas\" ] || { echo 'additionalInputs: \"../../lambdas\" must not exist yet. If you want to merge multiple artifacts, use a \"cp\" command.'; exit 1; } && mkdir -p -- \"../..\" && ln -s -- \"$CODEBUILD_SRC_DIR_owner_repo_Source\" \"../../lambdas\""
      ]
    },
    "build": {
      "commands": [
        "npm ci",
        "npm run build",
        "npx cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": "**/*"
  }
}
